package at.mschreiber.adventofcode;

import java.util.Arrays;
import java.util.concurrent.ArrayBlockingQueue;
import java.util.stream.Collectors;

import org.apache.commons.lang3.StringUtils;

public class Day6Excercise1 {

	public static void main(String args[]) {
		String input = "";

		ArrayBlockingQueue<String> queue = new ArrayBlockingQueue<String>(4);
		for (int i = 0; i < input.length(); i++) {
			if (queue.remainingCapacity() == 0) {
				String elements = Arrays.stream(queue.toArray(new String[0])).collect(Collectors.joining());
				if (isUnique(elements)) {
					System.out.println(i);
					break;
				}
				queue.poll();
			}
			queue.add(String.valueOf(input.charAt(i)));
		}

	}

	private static boolean isUnique(String elements) {
		for (char x : elements.toCharArray()) {
			if (StringUtils.countMatches(elements, x) > 1) {
				return false;
			}
		}
		return true;
	}
}
